$(document).ready(function() {
       (function() {
    $.stickysidebarscroll("#stick-me", {
        offset: {
            top: 10,
            bottom: 500
        }
    }),
    $("#owl-home").owlCarousel({
        navigation: !0,
        slideSpeed: 500,
        paginationSpeed: 7e3,
        singleItem: !0,
        autoPlay: 1e4,
        autoplayHoverPause: !0
    }),
    $("#owl-cat").owlCarousel({
        pagination: !1,
        items: 6,
        itemsDesktop: [1e3, 6],
        itemsDesktopSmall: [900, 5],
        itemsTablet: [600, 3],
        itemsMobile: [420, 2]
    }),
    $(".next").click(function() {
        $("#owl-cat").trigger("owl.next")
    }),
    $(".prev").click(function() {
        $("#owl-cat").trigger("owl.prev")
    }),
    $(".owl-carousel.owl-sidebar").owlCarousel({
        loop: !0,
        items: 1,
        autoHeight: !0,
        autoPlay: 5e3,
        slideSpeed: 1e3,
        paginationSpeed: 1e3,
        autoplayHoverPause: !0,
        itemsDesktop: [1199, 1],
        itemsDesktopSmall: [971, 1],
        itemsTablet: [970, 2],
        itemsTabletSmall: !1,
        itemsMobile: [479, 1]
    }),
    $(".owl-carousel.owl-product").owlCarousel({
        loop: !0,
        items: 3,
        autoHeight: !0,
        autoPlay: 5e3,
        slideSpeed: 1e3,
        paginationSpeed: 1e3,
        autoplayHoverPause: !0,
        itemsDesktop: [1199, 3],
        itemsDesktopSmall: [971, 3],
        itemsTablet: [970, 3],
        itemsTabletSmall: !1,
        itemsMobile: [479, 1]
    }),
    $("select").material_select(),
    $("#message").trigger("autoresize"),
    $(".modal-trigger").leanModal(),
    $(".button-collapse").sideNav({
        edge: "right",
        draggable: !1
    }),
    $(".filter-on-mob").is(":visible") && ($("#filter-slide").addClass("side-nav"),
    $(".filter-mob").sideNav({
        edge: "right",
        draggable: !1
    }));
    var e = $(".button-collapse-nav");
    e.click(function() {
        $(".filter-mob").sideNav("hide")
    }),
    $(".filter-on-mob, .filter-on-mob i, .login-mob-nav").click(function() {
        e.sideNav("hide")
    }),
    e.sideNav({
        edge: "left",
        draggable: !0
    }),
    $(".top-stores").hover(function() {
        $(".top-stores-mega").toggleClass("hide")
    }),
    $(".top-stores-mega").hover(function() {
        $(this).toggleClass("hide"),
        $(".top-stores a").toggleClass("nav-hover")
    }),
    $(".top-category").hover(function() {
        $(".top-category-mega").toggleClass("hide")
    }),
    $(".top-category-mega").hover(function() {
        $(this).toggleClass("hide"),
        $(".top-category a").toggleClass("nav-hover")
    }),
    $(".collapsible").collapsible({
        accordion: !1
    }),
    $(".top-desc").click(function() {
        $(this).toggleClass("expand")
    }),
    $(".coupon-desc").click(function() {
        $(this).toggleClass("expand")
    });
    var t = $(".scroll-top")
      , a = !1;
    $(document).scroll(function() {
        var e = $(this).scrollTop();
        e > 1200 && !a ? (a = !a,
        t.fadeIn()) : e <= 1200 && a && (a = !a,
        t.fadeOut())
    }),
    t.click(function() {
        return $("html, body").animate({
            scrollTop: 0
        }, "slow"),
        !1
    }),
    $("input[type=file].image-input").change(function() {
        $imgPreview = $(this).closest("div").find("img.preview"),
        $imgPreview && loadImagePreview(this, $imgPreview)
    }),
    $(document).on("click", "#toast-container .toast", function() {
        $(this).fadeOut(function() {
            $(this).remove()
        })
    }),
    $.ajaxSetup({
        headers: {
            "X-CSRF-TOKEN": $('meta[name="csrf-token"]').attr("content")
        }
    })
})
    });
